INSERT INTO wp_posts VALUES("218","1","2014-10-06 10:10:52","2014-10-06 10:10:52","<h1 class=\"rotherhambrand workstream-h1 lighttext\">WiFi</h1>\r\n<h3 style=\"color: #555555;\">Workstream Aim</h3>\r\n<p style=\"color: #555555;\">To identify the potential areas where collaboration on informatics systems, services or infrastructure between Trusts could take place</p>\r\n\r\n<h3>Project Aims</h3>\r\nEasy-to-use reciprocal Wi-Fi access across Trust sites for NHS supplied devices\r\n<h3>Project Benefits</h3>\r\n• Improves productivity of clinicians and managers who work\r\nacross more than one Trust\r\n• Supports re-design of pathway based and integrated care\r\nmodels\r\n• Reduces need for ad-hoc network access requests across\r\norganisations\r\n<h4>Project Updates</h4>","Wi-Fi","","inherit","closed","open","","199-revision-v1","","","2014-10-06 10:10:52","2014-10-06 10:10:52","","199","http://inch-reform.codio.io:3000/wordpress/199-revision-v1/","0","revision","","0");
