INSERT INTO wp_posts VALUES("220","1","2014-10-06 10:11:18","2014-10-06 10:11:18","<section class=\"post_content clearfix\">\r\n<h1 class=\"rotherhambrand workstream-h1 lighttext\">Cancer Waiting Times</h1>\r\n<h3 style=\"color: #555555;\">Workstream Aim</h3>\r\n<p style=\"color: #555555;\">To identify the potential areas where collaboration on informatics systems, services or infrastructure between Trusts could take place</p>\r\n\r\n<h3>Project Aims</h3>\r\n<p class=\"lead\">Implement inter-Trust solution to support 31/62 day cancer waiting time tracking</p>\r\n\r\n<h3>Project Benefits</h3>\r\nData transmitted electronically to another Trust as soon as it\r\nis recorded in local systems\r\n• Immediate access to data on patients under specific clinician\r\ncare in other Trusts\r\n• Reduced need for duplication of data entry\r\n<h4>Project Updates</h4>\r\n</section>&nbsp;","Cancer waiting times","","inherit","closed","open","","192-revision-v1","","","2014-10-06 10:11:18","2014-10-06 10:11:18","","192","http://inch-reform.codio.io:3000/wordpress/192-revision-v1/","0","revision","","0");
